useEffect(() => {
  let isMounted = true;
  const fetchData = async () => {
    if (isMounted && !isPreConfigured) {
      try {
        const response = await httpClient.get("api/1/configure/data");
        const configurationData = response.data;

        setConfigurationData((prevData) => ({
          ...prevData,
          [baseUrl]: configurationData.baseUrl,
