#include <iostream>
#include <vector>
using namespace std;

bool solve(vector<char>& mass, int pos, vector<int>& memo) {
    if (pos == mass.size() - 1) {
        return true;
    }
    if (memo[pos] != -1) {
        return memo[pos] == 1;
    }
    bool boolik = false;
    if (mass[pos] == '7') {
        // try moves to the left over ''?''
        for (int i = pos - 1; i >= 0 && mass[i] != 'X'; i--) {
            if (mass[i] == '.'){
                mass[pos] = '.';
                mass[i] = '7';
                boolik |= solve(mass, i, memo);
                mass[i] = '.';
                mass[pos] = '7';
            }
        }
        // try moves to the right over adjacent dots
        for (int i = pos + 1; i < mass.size() && mass[i] != 'X'; i++) {
            if (mass[i] == '.') {
                mass[pos] = '.';
                mass[i] = '7';
                boolik |= solve(mass, i, memo);
                mass[i] = '.';
                mass[pos] = '7';
            }
        }
    }
    memo[pos] = boolik ? 1 : 0;
    return boolik;
}

int main(){
    int n;
    cin>>n;
    string s;
    cin>>s;
    vector<char> mass;
    int start = -1;
    for (int i = 0; i < n; i++) {
        if (s[i] == 'X') {
            mass.push_back('.');
            start = i;
        }
        else {
            mass.push_back(s[i]);
        }
    }
    vector<int> memo(n, -1);
    if (start == n - 1) {
        cout<<"YES"<<endl;
    }
    else if (start != -1 && solve(mass, start, memo)) {
        cout<<"YES"<<endl;
    }
    else {
        cout<<"NO"<<endl;
    }
    return 0;
}

