class Card {
  constructor(name, cost, description) {
    this.name = name;
    this.cost = cost;
    this.description = description;
  }

  isPlayable(player) {
    return player.mana >= this.cost;
  }

  play() {
    console.log(`${this.name} is played!`);
  }
}

class StrikeCard extends Card {
  constructor() {
    super("Strike", 1, "Deal 6 damage to the enemy.");
  }

  isPlayable(player, enemy) {
    return super.isPlayable(player) && enemy.health > 0 && enemy instanceof Enemy;
  }

  play(enemy) {
    console.log(`${this.name} is played!`);
    enemy.takeDamage(6);
    console.log(`Dealing 6 damage to the enemy. Enemy health is now ${enemy.health}.`);
  }
}

class BlockCard extends Card {
  constructor() {
    super("Block", 1, "Gain 5 block.");
  }

  isPlayable(player) {
    return super.isPlayable(player);
  }

  play(player) {
    console.log(`${this.name} is played!`);
    player.gainBlock(5);
    console.log(`Gaining 5 block. Player block is now ${player.block}.`);
  }
}

class Enemy {
  constructor(health) {
    this.health = health;
  }

  takeDamage(damage) {
    this.health -= damage;
    if (this.health < 0) {
      this.health = 0;
    }
  }
}

class Player {
  constructor(block, mana) {
    this.block = block;
    this.mana = mana;
  }

  gainBlock(blockAmount) {
    this.block += blockAmount;
  }

  spendMana(cost) {
    this.mana -= cost;
  }
}

