    /**
     * Sorts an array of integers in ascending order using Bubble Sort algorithm
     * 
     * @param arr The array of integers to be sorted
     */
    public static void bubbleSort(int[] arr) {
        if (arr == null || arr.length <= 1) {
            return; // edge cases
        }

        int n = arr.length;
        for (int i = n - 1; i >= 1; i--) {
            for (int j = 0; j < i; j++) {
