<Annotations Target="YourService.YourEntitySet">
    <NavigationPropertyBinding Path="AssociatedEntity" Target="YourService.AssociatedEntitySet"/>
</Annotations>

// This will enable the SmartTable control to automatically fetch and display data from the associated entity set when you bind it to the main entity set.

// Finally, you can bind the SmartTable control to the main entity set in your controller:
oSmartTable.bindRows({
    path: "/YourEntitySet"
});

