template<typename T>
void parallel_for(int start, int end, int num_threads, T func) {
    const int chunk_size = (end - start + 1) / num_threads;
    std::vector<std::thread> threads(num_threads);
    for (int i = 0; i < num_threads-1; ++i) {
        const int thread_start = start + i * chunk_size;
        const int thread_end = thread_start + chunk_size - 1;
        threads[i] = std::thread(func, thread_start, thread_end);
    }
    threads[num_threads-1] = std::thread(func, start + (num_threads-1) * chunk_size, end);
    for (int i = 0; i < num_threads; ++i) {
        threads[i].join();
    }
}

void loop(int start, int end) {
    for (int i = start; i <= end; ++i) {
        std::cout << i << " ";
    }
}

int main() {
    const int start = 1;
    const int end = 100;
    const int num_threads = 4;
    parallel_for(start, end, num_threads, loop);
    return 0;
}
```

