class Inventory {
public:
    void add_item(int slot, const Item& item) {
        auto it = items_.find(slot);
        if (it != items_.end()) {
            it->second.set_quantity(std::min(it->second.quantity() + item.quantity(), it->second.max_quantity()));
        } else {
            items_[slot] = item;
        }
    }
    void remove_item(int slot, const Item& item) {
        auto it = items_.find(slot);
        if (it != items_.end()) {
            if (it->second.quantity() > item.quantity()) {
                it->second.set_quantity(it->second.quantity() - item.quantity());
            } else {
                items_.erase(it);
            }
        }
    }
