def gen_filename(fid, min_chars=1, charset=string.ascii_letters + string.digits):
	digits = []

	# Can't divide 0
	if fid == 0:
		digits.append(0)
	else:
		#Trope the entropy
		uniq = int(time.time()*(fid+1000))
		while uniq > 0:
			rem = uniq % len(charset)
			digits.append(rem)
			uniq /= len(charset)

	digits.reverse()
