```javascript
onSortByDatePressed: function (oEvent) {
  var oList = this.getView().byId("idList");
  var oBinding = oList.getBinding("items");

  // Check if a sorter is already applied
  var bSortActive = oBinding.aSorters.length > 0;

  if (!bSortActive) {
    // No sorter applied, sort by "assign_modified_date" in descending order
    var oSorter = new sap.ui.model.Sorter("assign_modified_date", true);
