#include "STM32F10x.h"

//#include <stm32f103x6.h>

#define SampleNumber 100
// INIT functions ------------------------
void GPIOInit(void);
void gpioIntInit(void);
void spi1Init(void);
void timer2Init(void);
void GenaralSramSetting(void);
// TRANSMITE DATA functions ---------------
uint16_t RamReader(void);
void RamWriter(uint16_t d);
void DAC(uint16_t d);
uint16_t ADC (void);
//GLOBAL data -----------------------------
uint16_t Counter=0;
uint16_t address=0x0000;
uint8_t flag1=1;
//Main function -------------------------------
int main(){

	GPIOInit();
	gpioIntInit();
	spi1Init();
	timer2Init();
	GenaralSramSetting();

	while(1){
	}
}
//GPIO AND GENERAL SETTING-------------------------------------------------
void GPIOInit(void){
	RCC->APB2ENR   =   RCC_APB2ENR_IOPAEN | RCC_APB2ENR_IOPBEN ;
	RCC->APB2RSTR |=   RCC_APB2RSTR_IOPARST | RCC_APB2RSTR_IOPBRST;
	RCC->APB2RSTR &= ~(RCC_APB2RSTR_IOPARST | RCC_APB2RSTR_IOPBRST);
	GPIOA->CRL     =   0xa4a42224 ;		//a5, a7 : spi1, a1, a2, a3 : output (NCS)
	GPIOA->ODR	  |=   0x00e ;
	GPIOB->CRL     =   0x22224444 ;		//b1: push button
	GPIOB->CRH	   =   0x22222222 ;

	}
//--------------------------------------------------------------
void gpioIntInit(void){

	RCC->APB2ENR   |=   0x00000001 ;
	AFIO->EXTICR[0] =0x0010;				//interrupt from port B pin 1
	EXTI->IMR   = (1<<1);					//PB1 unmasked
	//EXTI->EMR   = 0<<1;		     	//no event
	EXTI->FTSR  = (1<<1);					//falling edge interrupt
	//EXTI->RTSR  = 0<<1;	//rising edge(no)
	EXTI->PR    = (1<<1);					//clear pending interrupts
	//EXTI->SWIER = 0<<1;		//no software interrupt
	NVIC_EnableIRQ(EXTI1_IRQn);//This line enables the interrupt request for EXTI1_IRQn.
 //EXTI1 because interrupt is received through pin 1 og portB
}
//---------------------------------------------------------------
void EXTI1_IRQHandler(void){
	EXTI->PR    = (1<<1);//clear pending interrupts on PB1
	EXTI->IMR   = ~(1<<1);//PB1 is masked 
	flag1   =  0;
	address     =  0;
	}

//---------------------------------------------------------------

//------------------------------------------------------------------
	void timer2Init(void){
	RCC->APB1RSTR |=   RCC_APB1RSTR_TIM2RST;
	RCC->APB1RSTR &= ~(RCC_APB1RSTR_TIM2RST);//reset the TIM2 register
	RCC->APB1ENR  |=   RCC_APB1ENR_TIM2EN;//enable the TIM2 register
		// 1 kHz--1ms---------------------------------------
		//Ttimer=Tclock*prescalor*(autoreload+1)
	TIM2->PSC   = (40-1);		//prescaler
	TIM2->ARR   = (200-1);		//auto-reload
		//---------------------------------------------

	TIM2->DIER  = 0x0001;  // update interrupt enable 
	TIM2->CR1   = 0x0001;		//enable timer

	NVIC_EnableIRQ(TIM2_IRQn);
}
//---------------------------------------------------------------
void TIM2_IRQHandler(void){
	TIM2->SR &= ~0x00000001;  // This line clears the TIM2 interrupt flag. 
	// send data  as normal satae ------
	if(flag1==1){
		DAC(RamReader());
	}
	//-----------------------------------
	//  recieve data after intrupt ------
	else{
		RamWriter(ADC());
		Counter++;
		if(Counter==SampleNumber){
			flag1=1;
			Counter=0;
			address=0;
			EXTI->IMR   = (1<<1);					
		}
	}
	//-----------------------------------
}
// ----------------------------------------------------------
void GenaralSramSetting(void){
	GPIOB->ODR |= (0x0010);
	GPIOB->ODR &= ~(0x0010);
	SPI1->DR = 0x01;		//Write STATUS register
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); // sr-> flags 
	SPI1->DR = 0x40;		//Sequential mode 0100_0000
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);  // sr-> flags 
	GPIOB->ODR |= (0x0010);
}
//-------------------------------------------------------------------------------------------
void spi1Init(void){
	RCC->APB2RSTR |= RCC_APB2RSTR_SPI1RST;
	RCC->APB2RSTR &= ~RCC_APB2RSTR_SPI1RST;
	RCC->APB2ENR |= RCC_APB2ENR_SPI1EN;
	// faal sazi clk 

	SPI1->CR1 = 0x0044;	//0100_0100
	// bit 6 : spi anable 
	// bit 2 : mster selection 
	SPI1->CR2 = 0x0004;  // 0000 0100
	//1: SS output is enabled in master mode and when the cell is enabled. The cell cannot work

	}
//---------------------------------------------------------------------
uint16_t ADC(void){ // meghdar motale shode ra barmigardanad 
	uint16_t a;
	GPIOA->ODR &= ~(0x0002); // a1 = 1 faal kardan spi analog ti dig 
	SPI1->DR = 0x06;
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR = 0x40;
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY); // 4 bit vazn bala 
	a=((SPI1->DR & 0x000f)<<8);
	SPI1->DR = 0x00;
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);  // 8 bit vazn paeen 
	a |= (SPI1->DR & 0x00ff);
	GPIOA->ODR |= (0x0002);
	return(a);
}
//--------------------------------------------------------------------
void DAC(uint16_t d){
	uint16_t s;
	s = (1<<12) | (d&0x0fff);	//Gain=2, Shutdown=0  // 0001(d) 
	GPIOA->ODR &= ~(0x0008); // 0 kardan ss  
	SPI1->DR = ((s>>8)&0xff);  //   4 bit ersal moshvad 
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR =(s&0xff); // 8 bit ersal 
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR; // baray etmanan 
	GPIOA->ODR |= (0x0008); // 1 kardan ss 
}
//----------------------------------------------------------------------
uint16_t RamReader(void){

	uint16_t r;
	// REQUUEST ---------------------------------------------------------
	GPIOB->ODR &= ~(0x0010);  // b4 ss sram 
	SPI1->DR = 0x03;				//READ instruction
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR; // baray ok shodan flag 

	SPI1->DR = ((address>>8) & 0xff);		//Address(first 8 bits)
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR;
	SPI1->DR = (address & 0xff);	//Address(last 8 bits)
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);

	//---------------------------------------------------------------------

	//RECIEVE --------------------------------------------------------------
	SPI1->DR;
	SPI1->DR = 0x00;
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	r = (SPI1->DR)<<4; // 8  BIT vasn bala 
	SPI1->DR = 0x00;
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	r |= (SPI1->DR)>>4;
	GPIOB->ODR |= (0x0010); // 4 bit vazn paeen 
	address = address + 2;  // chon har seri 2 address pish mire 
	if(address>=2*SampleNumber){ // age be enteha reside bod az aavla 
		address = 0;
	}
	return(r);
}
//-------------------------------------------------------------------------------
void RamWriter(uint16_t d){
	//REQUEST-------------------------------------------------------------
	GPIOB->ODR &= ~(0x0010);
	SPI1->DR = 0x02;					//WRITE instruction
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR;
	SPI1->DR = ((address>>8) & 0xff);			//Address(first 8 bits)
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR;
	SPI1->DR = (address & 0xff);		//Address(last 8 bits)
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	//-------------------------------------------------------------------
	//SEND ---------------------------------------------------------------
	SPI1->DR;
	SPI1->DR = ((d>>4) & 0xff);
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR;
	SPI1->DR = ((d&0xf)<<4);
	while((SPI1->SR & SPI_SR_BSY) == SPI_SR_BSY);
	SPI1->DR;
	GPIOB->ODR |= (0x0010);
	address = address + 2;
}
