class _BouncingBallAppState extends State<BouncingBallApp> with SingleTickerProviderStateMixin {
  AnimationController _controller;
  Animation<Offset> _offsetAnimation;

  double ballDiameter = 50;
  double floor = 300;
  double ballPosition = 0;
  double ballVelocity = 0;
  double gravity = 9.8;

  @override
  void initState() {
    super.initState();
    _controller = AnimationController(vsync: this, duration: Duration(seconds: 10));
    _offsetAnimation = Tween<Offset>(
      begin: Offset.zero,
      end: const Offset(1.5, -1.0),
    ).animate(_controller)
      ..addListener(() {
        setState(() {
          ballPosition += ballVelocity;
          if (ballPosition >= floor - ballDiameter) {
            ballPosition = floor - ballDiameter;
            ballVelocity = -ballVelocity * 0.7;
          } else {
            ballVelocity += gravity / 30;
          }
        });
      });
    _controller.repeat(reverse: true);
  }

  @override
  void dispose() {
    _controller.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Bouncing Ball'),
      ),
      body: Center(
        child: Stack(
          children: <Widget>[
            Positioned(
              left: 0,
              right: 0,
              bottom: 0,
              child: Container(
                height: 10,
                color: Colors.grey[300],
              ),
            ),
            Positioned(
              bottom: ballPosition,
              child: SlideTransition(
                position: _offsetAnimation,
                child: Container(
                  width: ballDiameter,
                  height: ballDiameter,
                  decoration: BoxDecoration(
                    color: Colors.red,
                    borderRadius: BorderRadius.circular(ballDiameter / 2),
                  ),
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }
}

void main() => runApp(MaterialApp(home: BouncingBallApp()));
