  <script>
    window.onload = function () {
      setWidth(document.getElementsByTagName('td'))
      setWidth(document.getElementsByTagName('th'))

      table2Excel()
    }

    function setWidth(tds) {
      for (var i = 0; i < tds.length; i++) {
        var td = tds[i]
        var colspan = td.getAttribute('colspan') || 1
        td.setAttribute('width', (100 / 12 * colspan) + '%')
      }
    }

    function table2Excel() {
      var workbook = XLSX.utils.table_to_book(document.getElementById('ECR'));
      var binaryData = XLSX.write(workbook, { bookType: 'xlsx', type: 'binary' });

      function s2ab(s) {
        var buf = new ArrayBuffer(s.length);
        var view = new Uint8Array(buf);
        for (var i = 0; i < s.length; i++) view[i] = s.charCodeAt(i) & 0xFF;
        return buf;
      }

      var filename = (document.title || 'ECR') + '-' + new Date().getTime() + '.xlsx';

      var link = document.createElement('a');
      link.href = URL.createObjectURL(new Blob([s2ab(binaryData)], { type: "application/octet-stream" }));
      link.download = filename;
      link.click();
    }
  </script>
