	RCC->CFGR =   0x00000000;
	RCC->CIR  =   0x00000000;
	}

void GPIOInit(void){
	RCC->APB2ENR   =   0x0000000c ;
	RCC->APB2RSTR |=   0x0000000c ;
	RCC->APB2RSTR &= ~(0x0000000c);
	GPIOA->CRL     =   0x22222222 ;
	GPIOA->CRH     =   0x44444444 ;
	GPIOB->CRL     =   0x44442222 ;
	GPIOB->CRH     =   0x44444444 ;
	}

void timer3Init(void){
	RCC->APB1RSTR |= 0x0002;	//for timer 3
	RCC->APB1RSTR &= ~(0x0002);
	RCC->APB1ENR |=0x0002;//RCC_APB1ENR_TIM3EN
	TIM3->CR2 = 0x000;
	TIM3->SMCR = 0x000;
	TIM3->DIER = 0x001;
	TIM3->PSC = (2-1);//prescaler
	TIM3->ARR = (417-1);//auto-reload
	TIM3->CCR4 = 3906/100;
	TIM3->CCMR2 = 0xFC00;
	TIM3->CCER = 0x0000;
	TIM3->CR1 = 0x000;	//disable
	NVIC_EnableIRQ(TIM3_IRQn);
	}

void TIM3_IRQHandler(void){
	uint16_t a;
	TIM3->SR &= ~0x00000001;
	TIM3->ARR = (417-1);//auto-reload
	a=GPIOB->IDR&0x0040;//1<<rxdPin
	rData|=(a>>rdCount);
	if(rdCount!=0){
		rdCount--;
		}
	else{
		rxBusy = 0;
		rxDataReady=1;
		TIM3->CR1 = 0x000;	//disable
		EXTI->PR = (1<<rxdPin);				//clear pending interrupt
		EXTI->IMR  = (1<<rxdPin);//falling edge interrupt ensabled
		}
	}

void gpioIntInit(void){	//GPIO pin Interrrupt
	RCC->APB2ENR|=0x0001;
	AFIO->EXTICR[1]=0x0100;	//interrupt from port B pin 6 = rxdPin
	EXTI->IMR   = (1<<rxdPin);		//bit to generate interrup is defined by rxdPin
	EXTI->EMR   = 0<<rxdPin;			//no event
	EXTI->RTSR  = 0<<rxdPin;	//rising edge(no)
	EXTI->FTSR  = (1<<rxdPin);			//falling edge interrupt
	EXTI->PR    = (1<<rxdPin);				//clear pending interrupts
	EXTI->SWIER = 0<<rxdPin;		//no software interrupt
	NVIC_EnableIRQ(EXTI9_5_IRQn);
	}

void EXTI9_5_IRQHandler(void){
	EXTI->PR = (1<<rxdPin);				//clear pending interrupt
	TIM3->ARR = (625-1);//auto-reload = 1.5 bit
	EXTI->IMR  = 0x000;//interrupt disabled
	TIM3->CR1 = 0x001;	//enable
	rdCount=bits-1;
	rData=0;
	rxBusy=1;
	}

