import string

def password_checker(password):
    # Check length
    if len(password) < 14:
        return False

    # Check for at least one character from each character set
    if not any(c.isupper() for c in password):
        return False
    if not any(c.islower() for c in password):
        return False
    if not any(c.isdigit() for c in password):
        return False
    if not any(c in string.punctuation for c in password):
        return False

    # Check for no more than 3 consecutive characters from same set
    upper, lower, digit, punct = 0, 0, 0, 0
    for c in password:
        if c.isupper():
            upper += 1
            lower, digit, punct = 0, 0, 0
        elif c.islower():
            lower += 1
            upper, digit, punct = 0, 0, 0
        elif c.isdigit():
            digit += 1
            upper, lower, punct = 0, 0, 0
        elif c in string.punctuation:
            punct += 1
            upper, lower, digit = 0, 0, 0
        else:
            return False
        if any((upper > 3, lower > 3, digit > 3, punct > 3)):
            return False

    # Check for no whitespace
    if any(c in string.whitespace for c in password):
        return False

    # Valid password
    return True

