// Create a Library class to store and manage books
class Library {
  // Declare instance variables
  private Book[] books; // Array of books
  private int size; // Number of books in the library
  private final int CAPACITY = 100; // Maximum capacity of the library

  // Constructor
  public Library() {
    books = new Book[CAPACITY];
    size = 0;
  }

  // Method to add a book to the library
  public void addBook(Book book) {
    // Check if the book is null
    if (book == null) {
      System.out.println("Invalid book.");
      return;
    }
    // Check if the library is full
    if (size == CAPACITY) {
      System.out.println("The library is full.");
      return;
    }
    // Check if the book already exists in the library
    int index = findBook(book);
    if (index != -1) {
      // Update the quantity of the existing book
      books[index].setQuantity(books[index].getQuantity() + book.getQuantity());
      System.out.println("The book " + book.getTitle() + " by " + book.getAuthor() + " has been updated.");
    } else {
      // Add the book to the next available position
      books[size] = book;
      size++;
      System.out.println("The book " + book.getTitle() + " by " + book.getAuthor() + " has been added.");
    }
  }

  // Method to borrow a book from the library
  public void borrowBook(Book book, int num) {
    // Check if the book is null
    if (book == null) {
      System.out.println("Invalid book.");
      return;
    }
    // Check if the number of books to borrow is positive
    if (num <= 0) {
      System.out.println("Invalid number of books.");
      return;
    }
    // Check if the book exists in the library
    int index = findBook(book);
    if (index != -1) {
      // Check if the requested number of books is available
      if (books[index].getQuantity() >= num) {
        // Update the quantity of the book
        books[index].setQuantity(books[index].getQuantity() - num);
        System.out.println("You have borrowed " + num + " copies of " + book.getTitle() + " by " + book.getAuthor() + ".");
      } else {
        // Display an error message
        System.out.println("Sorry, there are only " + books[index].getQuantity() + " copies of " + book.getTitle() + " by " + book.getAuthor() + " available.");
      }
    } else {
      // Display an error message
      System.out.println("Sorry, the book " + book.getTitle() + " by " + book.getAuthor() + " is not in the library.");
    }
  }

  // Method to return a book to the library
  public void returnBook(Book book, int num) {
    // Check if the book is null
    if (book == null) {
      System.out.println("Invalid book.");
      return;
    }
    // Check if the number of books to return is positive
    if (num <= 0) {
      System.out.println("Invalid number of books.");
      return;
    }
    // Check if the book belongs to the library system
    int index = findBook(book);
    if (index != -1) {
      // Update the quantity of the book
      books[index].setQuantity(books[index].getQuantity() + num);
      System.out.println("You have returned " + num + " copies of " + book.getTitle() + " by " + book.getAuthor() + ".");
    } else {
      // Display an error message
      System.out.println("Sorry, the book " + book.getTitle() + " by " + book.getAuthor() + " does not belong to the library system.");
    }
  }

  // Method to find the index of a book in the library
  // Return -1 if the book is not found
  public int findBook(Book book) {
    // Loop through the books array
    for (int i = 0; i < size; i++) {
      // Compare the book with the current element
      if (books[i].equals(book)) {
        // Return the index if found
        return i;
      }
    }
    // Return -1 if not found
    return -1;
  }

  // Method to display the books in the library
  public void displayBooks() {
    // Check if the library is empty
    if (size == 0) {
      System.out.println("The library is empty.");
      return;
    }
    // Loop through the books array
    for (int i = 0; i < size; i++) {
      // Print the book information
      System.out.println(books[i]);
    }
  }
}

