useEffect(() => {
  const fetchData = async () => {
    try {
      const response = await httpClient.get("api/1/configure/data");
      const configurationData = response.data;

      setConfigurationData((prevData) => ({
        ...prevData,
        [baseUrl]: configurationData.baseUrl,
      }));
      setWhatsAppEnabled(configurationData.whatsAppConfig.enabled ?? false);
      setwhatsAppInboundEnabled(
        configurationData.whatsAppConfig.inboundEnabled ?? false
      );
