  pumpingTime = time;
}

void startPumping() {
  pumpState = true;
  valveState = true;
  digitalWrite(pumpPin, HIGH);
  delay(500); 
  digitalWrite(valvePin, LOW);
  flowRate = 0;
  startTime = millis();
  Serial.println("Начало перекачки");
}

void stopPumping() {
  pumpState = false;
  valveState = false;
  pauseState = false;
  digitalWrite(pumpPin, LOW);
  digitalWrite(valvePin, HIGH);
  Serial.println("Остановка перекачки");
}

void pausePumping() {
  pauseState = true;
  digitalWrite(pumpPin, LOW);
  digitalWrite(valvePin, HIGH);
  pauseStartTime = millis();
  Serial.println("Пауза перекачки");
}

void resumePumping() {
  pauseState = false;
  digitalWrite(pumpPin, HIGH);
  delay(500);
  digitalWrite(valvePin, LOW);
  startTime += (millis() - pauseStartTime);
  Serial.println("Возобновление перекачки");
}

void checkPumpedVolume() {
  if (flowRate >= volumeToPump) {
    stopPumping();
    Serial.print("Перекачано: ");
    Serial.print(flowRate);
    Serial.println(" литров");
  }
}
