  valveState = true;
  digitalWrite(pumpPin, HIGH);
  delay(500); 
  digitalWrite(valvePin, LOW);
  flowRate = 0;
  startTime = millis();
  Serial.println("Начало перекачки");
}

void stopPumping() {
  pumpState = false;
  valveState = false;
  pauseState = false;
  digitalWrite(pumpPin, LOW);
  digitalWrite(valvePin, HIGH);
  Serial.println("Остановка перекачки");
}

void pausePumping() {
  pauseState = true;
  digitalWrite(pumpPin, LOW);
  digitalWrite(valvePin, HIGH);
  pauseStartTime = millis();
  Serial.println("Пауза перекачки");
}

void resumePumping() {
  pauseState = false;
  digitalWrite(pumpPin, HIGH);
  delay(500);
  digitalWrite(valvePin, LOW);
  pauseDuration += (millis() - pauseStartTime);
  Serial.println("Возобновление перекачки");
}

void checkPumpingProgress() {
  if (pumpState && !pauseState && flowRate >= targetVolume) {
    stopPumping();
    Serial.print("Перекачано: ");
    Serial.print(flowRate / 1000.0);
    Serial.println(" литров");
  }
  if (pumpState && !pauseState && (millis() - startTime - pauseDuration) >= targetTime) {
    stopPumping();
    Serial.print("Прошло заданное время: ");
    Serial.print(targetTime / 60000);
    Serial.println(" минут");
  }
}
```

В этом коде я добавил переменную `pauseDuration` для расчета суммарной продолжительности пауз, чтобы учесть только активное время перекачки, а также добавлены кнопки "Старт", "Стоп" и "Пауза" для управления перекачкой. Для задания объема и времени перекачки используется функция `setPumpingParameters`.

