using System.Collections;
using UnityEngine;

public class AIFighter : MonoBehaviour
{
    public float moveSpeed = 2.0f; // AI移动速度
    public float attackRange = 2.0f; // AI攻击范围
    public float attackCooldown = 1.0f; // AI攻击冷却时间
    public float attackDamage = 10.0f; // AI攻击力

    private Transform target; // 目标玩家
    private bool isAttacking = false; // 是否正在攻击
    private Animator animator; // Animator组件（用于播放动画）

    void Start()
    {
        target = GameObject.FindGameObjectWithTag("Player").transform;
        animator = GetComponent<Animator>();
    }

    void FixedUpdate()
    {
        if (isAttacking) return;
        MoveTowardPlayer();
    }

    void MoveTowardPlayer()
    {
        float step = moveSpeed * Time.deltaTime;
        transform.position = Vector3.MoveTowards(transform.position, target.position, step);

        if (Vector3.Distance(transform.position, target.position) <= attackRange) 
        {
            StartCoroutine(PerformAttack());
        }
    }

    IEnumerator PerformAttack()
    {
        isAttacking = true;

        // 播放攻击动画（通常需要设置一个Trigger参数来启动攻击动画）
        animator.SetTrigger("Attack");

        // 请确保您的玩家角色具有“PlayerHealth”的脚本，用于处理生命值变化。
        PlayerHealth playerHealth = target.GetComponent<PlayerHealth>();
        if (playerHealth != null)
        {
            playerHealth.TakeDamage(attackDamage);
        }

        yield return new WaitForSeconds(attackCooldown);
        isAttacking = false;
    }
}
```

