```csharp
using UnityEngine;
using UnityEngine.AI;

public class AIController : MonoBehaviour
{
    public Transform target;
    public float moveSpeed = 5f;
    public float attackThreshold = 0.5f;
    public float[] attackDelays;
    public string[] attackTriggers;
    public float attackRange = 3f;
    public float timeBetweenAttacks = 1f;

    private NavMeshAgent agent;
    private Animator animator;
    private float timer;

    private void Start()
    {
        agent = GetComponent<NavMeshAgent>();
        animator = GetComponent<Animator>();
        timer = timeBetweenAttacks;
    }

    private void Update()
    {
        if (target != null)
        {
            agent.SetDestination(target.position);
            float distanceToTarget = Vector3.Distance(transform.position, target.position);

            if (distanceToTarget < attackRange)
            {
                Attack();
            }
            else
            {
                animator.SetBool("IsRunning", true);
            }
        }
    }

    private void Attack()
    {
        agent.velocity = Vector3.zero;
        animator.SetBool("IsRunning", false);
        timer += Time.deltaTime;

        if (timer >= timeBetweenAttacks)
        {
            timer = 0;
            int attackIndex = Random.Range(0, attackDelays.Length);

            if (animator.GetCurrentAnimatorStateInfo(0).normalizedTime % 1 > attackThreshold)
            {
                animator.SetFloat("AttackDelay", attackDelays[attackIndex]);
                animator.SetTrigger(attackTriggers[attackIndex]);
            }
        }
    }
}
```

