    int turn = 0;

    initialize_board(board, &turn);
    print_board(board);

    while (!is_winner(BLACK, board) && !is_winner(WHITE, board)) {
        printf("Player %d Turn: \nEnter the row and column for your move: ", (turn % 2) + 1);
        int row, col;
        scanf("%d%d", &row, &col);

        if (move((turn % 2) + 1, board, row - 1, col - 1)) {
            turn++;
            print_board(board);
        }
        else {
            printf("Invalid Move. Try Again.\n");
        }
    }

    printf("Player %d Wins!\n", (turn % 2) + 1);

    return 0;
}

void initialize_board(player board[ROWS][COLS], int* turn) {
    int i, j;
    for (i = 0; i < ROWS; i++) {
        for (j = 0; j < COLS; j++) {
            board[i][j] = EMPTY;
        }
    }
    printf("Player 1: Fill the top row with your pieces.\n");
    while (getchar() != '\n');
    int row = 0;
    for (i = 0; i < 16; i++) {
        print_board(board);
        printf("Enter piece %d location (format: row col): ", i + 1);
        int x, y;
        scanf("%d %d", &x, &y);
        if (row == 0 && x - 1 != row) {
            printf("Pieces must be placed in the top row.\n");
            i--;
            continue;
        }
        if (row == ROWS - 1 && x - 1 != row) {
            printf("Pieces must be placed in the bottom row.\n");
            i--;
            continue;
        }
        if (is_valid_move(BLACK, board, x - 1, y - 1)) {
            board[x - 1][y - 1] = BLACK;
        }
        else {
            printf("Invalid Move. Try Again.\n");
            i--;
        }
        if (i == 7) {
            printf("Player 2: Fill the bottom row with your pieces.\n");
            while (getchar() != '\n');
            row = ROWS - 1;
            *turn = 1;
        }
    }
    printf("Board successfully configured...Start the game.\n");
}

void print_board(player board[ROWS][COLS]) {
    int i, j;

    printf("\n   ");
    for (i = 0; i < ROWS; i++) {
        printf(" %d ", i + 1);
    }

    printf("\n  ");
    for (i = 0; i < ROWS + (ROWS - 1); i++) {
        printf("-");
    }

    for (i = 0; i < ROWS; i++) {
        printf("\n%d|", i + 1);
        for (j = 0; j < COLS; j++) {
            if (board[i][j] == EMPTY) {
                printf(" - ");
            }
            else if (board[i][j] == BLACK) {
                printf(" x ");
            }
            else {
                printf(" o ");
            }
        }
        printf("|%d", i + 1);
    }

    printf("\n  ");
    for (i = 0; i < ROWS + (ROWS - 1); i++) {
        printf("-");
    }
    printf("\n   ");
    for (i = 0; i < ROWS; i++) {
        printf(" %d ", i + 1);
    }
    printf("\n\n");
}

int is_valid_move(player player_turn, player board[ROWS][COLS], int x, int y) {
    if (board[x][y] == player_turn || (x > 0 && board[x - 1][y] == player_turn) || (x < ROWS - 1 && board[x + 1][y] == player_turn) || (y > 0 && board[x][y - 1] == player_turn) || (y < COLS - 1 && board[x][y + 1] == player_turn)) {
        if (board[x][y] == EMPTY) {
            return 1;
        }
    }
    return 0;
}

int move(player player_turn, player board[ROWS][COLS], int x, int y) {
    if (is_valid_move(player_turn, board, x, y)) {
        board[x][y] = player_turn;
        return 1;
    }
    else {
        return 0;
    }
}

int is_winner(player player_turn, player board[ROWS][COLS]) {
    int i, j;

    // horizontal check
    for (i = 0; i < ROWS; i++) {
        for (j = 0; j < COLS - 2; j++) {
            if (board[i][j] == player_turn && board[i][j + 1] == player_turn && board[i][j + 2] == player_turn) {
                return 1;
            }
        }
    }

    // vertical check
    for (i = 0; i < ROWS - 2; i++) {
        for (j = 0; j < COLS; j++) {
            if (board[i][j] == player_turn && board[i + 1][j] == player_turn && board[i + 2][j] == player_turn) {
                return 1;
            }
        }
    }

    // diagonal check
    for (i = 0; i < ROWS - 2; i++) {
        for (j = 0; j < COLS - 2; j++) {
            if (board[i][j] == player_turn && board[i + 1][j + 1] == player_turn && board[i + 2][j + 2] == player_turn) {
                return 1;
            }
            else if (board[i + 2][j] == player_turn && board[i + 1][j + 1] == player_turn && board[i][j + 2] == player_turn) {
                return 1;
            }
        }
    }

    // no winner
    return 0;
}
