```cpp
#include <iostream>
#include <conio.h>
#include <windows.h>
#include <vector>

using namespace std;

const int width = 50;
const int height = 20;
const int size = width * height;

int headPosition = size / 2 + width / 2;

bool gameOver = false;

int score = 0;
vector<int> tail;

enum direction {UP, DOWN, LEFT, RIGHT};
direction dir;

void Setup()
{
    dir = LEFT;
}

void Draw()
{
    system("cls"); // clear console

    for (int i = 0; i < size; i++)
    {
        if (i % width == 0) cout << endl;

        if (i == headPosition) cout << "0";
        else if (i == rand() % size) cout << "F";
        else if (find(tail.begin(), tail.end(), i) != tail.end()) cout << "o";
        else cout << " ";
    }

    cout << endl << "Score: " << score << endl;
}

void Input()
{
    if (_kbhit())
    {
        switch(_getch())
        {
            case 'w':
                dir = UP;
                break;
            case 's':
                dir = DOWN;
                break;
            case 'a':
                dir = LEFT;
                break;
            case 'd':
                dir = RIGHT;
                break;
            case 'x':
                gameOver = true;
                break;
        }
    }
}

void Logic()
{
    int prevPosition = tail.empty() ? headPosition : tail.back();
    int x = prevPosition % width;
    int y = prevPosition / width;

    switch(dir)
    {
        case UP:
            y--;
            break;
        case DOWN:
            y++;
            break;
        case LEFT:
            x--;
            break;
        case RIGHT:
            x++;
            break;
    }

    int newPosition = y * width + x;

    if (find(tail.begin(), tail.end(), newPosition) != tail.end()) {
        gameOver = true;
        return;
    }

    tail.insert(tail.begin(), headPosition);

    while (tail.size() > score + 1) {
        tail.pop_back();
    }

    headPosition = newPosition;

    if (headPosition == tail.back())
    {
        tail.pop_back();
    }

    if (headPosition == rand() % size)
    {
        score++;
    }
}

int main()
{
    Setup();

    while (!gameOver)
    {
        Draw();
        Input();
        Logic();

        Sleep(100);
    }

    return 0;
}
```

