    switch(dir)
    {
        case UP:
            y--;
            break;
        case DOWN:
            y++;
            break;
        case LEFT:
            x--;
            break;
        case RIGHT:
            x++;
            break;
    }

    int newPosition = y * width + x;

    if (find(tail.begin(), tail.end(), newPosition) != tail.end()) {
        gameOver = true;
        return;
    }

    tail.insert(tail.begin(), headPosition);

    while (tail.size() > score + 1) {
        tail.pop_back();
    }

    headPosition = newPosition;

    if (headPosition == tail.back())
    {
        tail.pop_back();
    }

    if (headPosition == rand() % size)
    {
        score++;
    }
}

int main()
{
    Setup();

    while (!gameOver)
    {
        Draw();
        Input();
        Logic();

        usleep(100000); // sleep for 100 milliseconds
    }

    endwin(); // end ncurses

