#include <Adafruit_SSD1306.h>
#include <Adafruit_GFX.h>
#include <Adafruit_SSD1306.h>
#include <Adafruit_NeoPixel.h>
#include <Adafruit_NeoMatrix.h>
#include <Adafruit_GFX.h>
#include <Adafruit_NeoPixel.h>
#include <Adafruit_NeoMatrix.h>

#define PIN 6
#define JOY_VERT A1
#define JOY_HORIZ A0
#define JOY_SEL 2

Adafruit_NeoMatrix matrix = Adafruit_NeoMatrix(8, 8, PIN,
  NEO_MATRIX_TOP     + NEO_MATRIX_LEFT +
  NEO_MATRIX_ROWS + NEO_MATRIX_PROGRESSIVE,
  NEO_GRB            + NEO_KHZ800);

int x = 3;
int y = 3;
int dx = 1;
int dy = 0;
int foodX = 5;
int foodY = 5;
int score = 0;
int speed = 200;

void setup() {
  matrix.begin();
  matrix.setTextWrap(false);
  matrix.setBrightness(50);
  matrix.setTextColor(matrix.Color(255, 255, 255));
  randomSeed(analogRead(0));
}

void loop() {
  int vert = analogRead(JOY_VERT);
  int horiz = analogRead(JOY_HORIZ);
  int sel = digitalRead(JOY_SEL);

  if (sel == LOW) {
    dx = 0;
    dy = 0;
  } else if (horiz < 470) {
    dx = -1;
    dy = 0;
  } else if (horiz > 550) {
    dx = 1;
    dy = 0;
  } else if (vert < 470) {
    dx = 0;
    dy = -1;
  } else if (vert > 550) {
    dx = 0;
    dy = 1;
  }

  x += dx;
  y += dy;

  if (x < 0) {
    x = 7;
  } else if (x > 7) {
    x = 0;
  }

  if (y < 0) {
    y = 7;
  } else if (y > 7) {
    y = 0;
  }

  if (x == foodX && y == foodY) {
    score++;
    speed -= 10;
    foodX = random(0, 8);
    foodY = random(0, 8);
  }

  matrix.fillScreen(0);
  matrix.drawPixel(foodX, foodY, matrix.Color(255, 0, 0));
  matrix.drawPixel(x, y, matrix.Color(0, 255, 0));
  matrix.show();
  delay(speed);
}
```

