``` python
import random

# 初始化游戏函数
def initGame(N, bombNum):
    # 初始化网格
    grid = [[0 for j in range(N)] for i in range(N)]
    bombs = []
    # 在随机位置放置炸弹
    for n in range(bombNum):
        row = random.randint(0, N-1)
        col = random.randint(0, N-1)
        while (row, col) in bombs:
            row = random.randint(0, N-1)
            col = random.randint(0, N-1)
        bombs.append((row, col))
        grid[row][col] = -1
    # 在地图上标注周围的炸弹数
    for i in range(N):
        for j in range(N):
            if (i, j) not in bombs:
                count = 0
                if i-1 >= 0 and j-1 >= 0 and grid[i-1][j-1] == -1:
                    count += 1
                if i-1 >= 0 and grid[i-1][j] == -1:
                    count += 1
                if i-1 >= 0 and j+1 < N and grid[i-1][j+1] == -1:
                    count += 1
                if j-1 >= 0 and grid[i][j-1] == -1:
                    count += 1
                if j+1 < N and grid[i][j+1] == -1:
                    count += 1
                if i+1 < N and j-1 >= 0 and grid[i+1][j-1] == -1:
                    count += 1
                if i+1 < N and grid[i+1][j] == -1:
                    count += 1
                if i+1 < N and j+1 < N and grid[i+1][j+1] == -1:
                    count += 1
                grid[i][j] = count
    return grid

# 打印游戏地图
def printGrid(grid):
    for i in range(len(grid)):
        for j in range(len(grid)):
            if grid[i][j] >= 0:
                print(grid[i][j], end=" ")
            else:
                print("*", end=" ")
        print()

# 点开空方块函数
def openEmpty(i, j, grid, visited):
    if (i, j) in visited:
        return
    visited.append((i, j))
    grid[i][j] = -2
    if i-1 >= 0 and j-1 >= 0 and grid[i-1][j-1] == 0:
        openEmpty(i-1, j-1, grid, visited)
    if i-1 >= 0 and grid[i-1][j] == 0:
        openEmpty(i-1, j, grid, visited)
    if i-1 >= 0 and j+1 < len(grid) and grid[i-1][j+1] == 0:
        openEmpty(i-1, j+1, grid, visited)
    if j-1 >= 0 and grid[i][j-1] == 0:
        openEmpty(i, j-1, grid, visited)
    if j+1 < len(grid) and grid[i][j+1] == 0:
        openEmpty(i, j+1, grid, visited)
    if i+1 < len(grid) and j-1 >= 0 and grid[i+1][j-1] == 0:
        openEmpty(i+1, j-1, grid, visited)
    if i+1 < len(grid) and grid[i+1][j] == 0:
        openEmpty(i+1, j, grid, visited)
    if i+1 < len(grid) and j+1 < len(grid) and grid[i+1][j+1] == 0:
        openEmpty(i+1, j+1, grid, visited)

# 执行游戏函数
def playGame(N, bombNum):
    grid = initGame(N, bombNum)
    print("开始扫雷游戏！")
    while True:
        printGrid(grid)
        row = int(input("请输入要翻开的行数（从0开始）："))
        col = int(input("请输入要翻开的列数（从0开始）："))
        if grid[row][col] == -1:
            printGrid(grid)
            print("很不幸，你踩中雷，游戏结束！")
            break
        elif grid[row][col] == 0:
            visited = []
            openEmpty(row, col, grid, visited)
        else:
            grid[row][col] = -2
        count = 0
        for i in range(len(grid)):
            for j in range(len(grid)):
                if grid[i][j] >= 0:
                    count += 1
        if count == N*N - bombNum:
            printGrid(grid)
            print("恭喜，你成功扫雷！")
            break

# 呼叫游戏函数，设定地图大小和炸弹数
playGame(10, 10)
