using System;
using Verse;
using Verse.AI;
using RimWorld;
namespace AdvancedRailgun {
    public class ThingDef_AdvancedRailgun : ThingDef {
        public float reloadTime = 10.0f;
        public float accuracy = 0.95f;
        public float range = 60.0f;
        public float armorPenetrationSharp = 0.75f;
        public float armorPenetrationBlunt = 0.1f;
    }
    public class Thing_AdvancedRailgun : ThingWithComps {
        public int ammoCount = 1;
        public int maxAmmoCount = 1;
        public bool reloading = false;
        public bool needsReload = true;
        public CompEquippable compEquippable;
        public ThingDef_AdvancedRailgun def => base.def as ThingDef_AdvancedRailgun;
        public override void SpawnSetup(Map map, bool respawningAfterLoad) {
            base.SpawnSetup(map, respawningAfterLoad);
            this.compEquippable = base.GetComp<CompEquippable>();
            this.maxAmmoCount = 1;
        }
        public override void TickRare() {
            base.TickRare();
            if (!this.needsReload && this.ammoCount <= 0) {
                this.needsReload = true;
                this.reloading = false;
            }
            if (this.reloading && this.needsReload && this.AmmoRequired() == 0) {
                this.ammoCount = this.maxAmmoCount;
                this.needsReload = false;
                this.reloading = false;
            }
        }
        public override void Tick() {
            base.Tick();
            if (this.reloading && !(this is Pawn_EquipmentTracker)) {
                if (this.AmmoRequired() > 0) {
                    this.compEquippable.AllVerbs.ForEach((Verb v) => {
                        if (v.verbProps.isPrimary) {
                            v.TicksUntilCleared = (int)Math.Ceiling(v.verbProps.defaultCooldownTime * 1.3f);
                        }
                    });
                }
                if (this.AmmoRequired() == 0 && this.ammoCount >= this.maxAmmoCount) {
                    this.needsReload = false;
                    this.reloading = false;
                }
            }
        }
        public bool TryReload(Pawn pawn) {
            if (this.needsReload && !this.reloading) {
                var desiredAmmoCount = Math.Min(pawn.inventory.innerContainer.Count<Thing>(x => x.def == ThingDefOf.ComponentIndustrial && x.stackCount == 1), Math.Min(this.maxAmmoCount - this.ammoCount, 5));
                if (desiredAmmoCount > 0) {
                    var ammoToTake = pawn.inventory.innerContainer.Where(x => x.def == ThingDefOf.ComponentIndustrial && x.stackCount == 1).Take(desiredAmmoCount).ToArray();
                    if (ammoToTake.Length > 0) {
                        ammoToTake.ForEach((Thing t) => pawn.inventory.innerContainer.Remove(t));
                        this.ammoCount += desiredAmmoCount;
                        this.reloading = true;
                        return true;
                    }
                }
            }
            return false;
        }
        public int AmmoRequired() {
            return Math.Max(0, this.maxAmmoCount - this.ammoCount);
        }
        public override void DrawExtraSelectionOverlays() {
            base.DrawExtraSelectionOverlays();
            if (this.reloading) {
                GenDraw.FillableBarRequest r = new GenDraw.FillableBarRequest() {
                    center = this.DrawPos + Vector3.up * 0.1f,
                    size = new Vector2(0.5f, 0.5f),
                    fillPercent = 0f,
                    filledMat = SolidColorMaterials.SimpleSolidColorMaterial(new Color(1, 1, 1, 0.5f)),
                    unfilledMat = SolidColorMaterials.SimpleSolidColorMaterial(new Color(0, 0, 0, 0.2f)),
                    margin = 0.15f,
                    rotation = Rot4.North
                };
                if (this.AmmoRequired() == 1) {
                    r.fillPercent = 0.5f;
                } else if (this.AmmoRequired() == 2) {
                    r.fillPercent = 0.7f;
                } else if (this.AmmoRequired() == 3) {
                    r.fillPercent = 0.85f;
                } else if (this.AmmoRequired() == 4) {
                    r.fillPercent = 0.95f;
                } else if (this.AmmoRequired() == 5) {
                    r.fillPercent = 1.0f;
                }
                GenDraw.DrawFillableBar(r);
            }
        }
        public override Verb TryGetAttackVerb(Thing target, bool allowManualCastModes = false) {
            var verbProperties = new VerbProperties() {
                hasStandardCommand = true,
                range = this.def.range,
                accuracyMult = this.def.accuracy,
                warmupTime = 1.5f,
                defaultCooldownTime = this.def.reloadTime,
                shootingDelay = 6,
                burstShotCount = 1,
                stopBurstWithoutLos = false,
                minRange = 0,
                requireLineOfSight = true,
                meleeDamageDef = null,
                projectileDef = ThingDef.Named("Projectile_AdvancedRailgun"),
                surpriseAttack = false,
                ai_IsWeapon = true,
                soundCast = SoundDef.Named("AdvancedRailgun_Shot"),
                soundCastTail = SoundDef.Named("AdvancedRailgun_Shot_Tail"),
                soundMiss = SoundDef.Named("Bullet_Miss")
            };
            var attackVerb = new Verb() {
                verbProps = verbProperties,
                caster = this,
                verbTracker = this.verbTracker
            };
            if (target != null) {
                var playerTarget = target as Pawn;
                if (playerTarget != null && playerTarget.RaceProps != null && playerTarget.RaceProps.IsFlesh) {
                    attackVerb.verbProps.projectileDef = ThingDef.Named("Projectile_AdvancedRailgun_Flesh");
                } else {
                    attackVerb.verbProps.projectileDef = ThingDef.Named("Projectile_AdvancedRailgun");
                }
                if (this.ammoCount <= 0 && this.needsReload) {
                    attackVerb.Disable("ReloadRequired".Translate() + ".");
                }
            } else {
                attackVerb.verbProps.projectileDef = ThingDef.Named("Projectile_AdvancedRailgun");
            }
            return attackVerb;
        }
    }
    public class ModCore : Mod {
        public ModCore(ModContentPack content) : base(content) {
        }
    }
    public class Projectile_AdvancedRailgun : Bullet {
        protected override void Impact(Thing hitThing) {
            base.Impact(hitThing);
            var pawnTarget = hitThing as Pawn;
            var damageDef = DamageDefOf.Bomb;
            var damageAmountBase = 1000.0f;
            var damageAmount = damageAmountBase * this.def.projectile.damageFactor;
            var damageType = DamageDefOf.EMP;
            if (pawnTarget != null) {
                armorPenetrationSharp = DefDatabase<ThingDef_AdvancedRailgun>.GetNamed("AdvancedRailgun").armorPenetrationSharp;
                armorPenetrationBlunt = DefDatabase<ThingDef_AdvancedRailgun>.GetNamed("AdvancedRailgun").armorPenetrationBlunt;
                var bodyPartGroupDef = new BodyPartGroupDef() {defName = "Torso"};
                var num = 1.0f / Math.Max(0.1f, pawnTarget.apparel.GetStatValueAbstract(StatDefOf.ArmorRating_Sharp, bodyPartGroupDef));
                damageAmount *= num;
                hitThing.TakeDamage(new DamageInfo(damageDef, damageAmount, armorPenetrationSharp, armorPenetrationBlunt, null, null, this), null);
            } else {
                hitThing.TakeDamage(new DamageInfo(damageDef, damageAmount, armorPenetrationSharp, armorPenetrationBlunt, null, null, this), null);
            }
        }
    }
    public class Projectile_AdvancedRailgun_Flesh : Projectile_AdvancedRailgun {
        protected override void Impact(Thing hitThing) {
            base.Impact(hitThing);
            if (hitThing is Pawn) {
                if (!((Pawn)hitThing).health.hediffSet.HasHediff(HediffDef.Named("Death"))) {
                    ((Pawn)hitThing).health.hediffSet.AddDirect(HediffMaker.MakeHediff(HediffDef.Named("Death"), hitThing, Vec2));
                }
            } else {
                var damageDef = DamageDefOf.Bomb;
                var damageAmountBase = 100.0f;
                var damageAmount = damageAmountBase * this.def.projectile.damageFactor;
                var armorPenetrationSharp = DefDatabase<ThingDef_AdvancedRailgun>.GetNamed("AdvancedRailgun").armorPenetrationSharp;
                var armorPenetrationBlunt = DefDatabase<ThingDef_AdvancedRailgun>.GetNamed("AdvancedRailgun").armorPenetrationBlunt;
                hitThing.TakeDamage(new DamageInfo(damageDef, damageAmount, armorPenetrationSharp, armorPenetrationBlunt, null, null, this), null);
            }
        }
    }
}

