        for (const card of cards) {
          if (ranks.indexOf(card.rank) <= topRank && ranks.indexOf(card.rank) >= topRank - 4) {
            combinationCards.push(card);
          }
        }
        break;
      }
    } else {
      straight = 0;
    }
    i++;
  }

  for (let twoPair = 0, i = 12; i >= 0; i--) {
    for (let j = 0; j < rankCounts[i] && combinationCards.length < 5; j++) {
      if (combinationRank && combinationRank != 1 && combinationRank != 3) {
        break;
      }
      combinationCards.unshift(cards.find(card => card.rank === ranks[i]));
    }
    if (rankCounts[i] === 4) {
      combinationRank = combinationRank || 7;
    } else if (rankCounts[i] === 3) {
      combinationRank = combinationRank || 3;
    } else if (rankCounts[i] === 2) {
      combinationRank = (combinationRank || 1) + twoPair++ * 2;
    }
  }

  if (straight && flush) {
    combinationRank = flush.cards.findIndex(card => card.rank === ranks[straight + (straight === 4 ? -1 : 0)]) >= 0 ? 9 : 8;
    combination = combinationRank === 9 ? 'royal flush' : 'straight flush';
    combinationCards = flush.cards.slice(0, 5);
  } else if (flush) {
    combinationRank = 5;
    combination = 'flush';
    combinationCards = flush.cards.slice(0, 5);
  } else if (straight) {
    combinationRank = 4;
    combination = 'straight';
  } else {
    const combinations = ['', 'pair', 'two pair', 'three of a kind', 'straight', 'flush', 'full house', 'four of a kind', 'straight flush', 'royal flush'];
    combination = combinations[combinationRank];
  }

  return { combinationRank, combination, cards: combinationCards };
}
```

