private IEnumerator JumpHeadBob()
{
    float headBobDuration = 0.5f; // Adjust this value for desired headbob duration
    float timeElapsed = 0;
    Vector3 initialCameraPosition = playerCamera.transform.localPosition;
    Quaternion initialCameraRotation = playerCamera.transform.localRotation;

    while (timeElapsed < headBobDuration)
    {
        float yOffset = Mathf.Sin(timeElapsed / headBobDuration * Mathf.PI) * 0.1f; // 0.1f is the amplitude, adjust it for desired headbob height
        playerCamera.transform.localPosition = new Vector3(initialCameraPosition.x, initialCameraPosition.y + yOffset, initialCameraPosition.z);

        float zRotation = Mathf.Sin(timeElapsed / headBobDuration * Mathf.PI * 2) * 3; // 3 is the amplitude, adjust it for desired head rotation
        playerCamera.transform.localRotation = initialCameraRotation * Quaternion.Euler(0, 0, zRotation);

        timeElapsed += Time.deltaTime;
        yield return null;
    }

    playerCamera.transform.localPosition = initialCameraPosition;
    playerCamera.transform.localRotation = initialCameraRotation;
}
