const double PI = 3.14159265358979323846;

void drawHeart()
{
    double x, y, size, angle;
    double step = PI / 180.0;
    int i, j, k;

    for (i = 0; i < 360; i++)
    {
        angle = i * step;
        x = 16 * pow(sin(angle), 3);
        y = -(13 * cos(angle) - 5 * cos(2 * angle) - 2 * cos(3 * angle) - cos(4 * angle));
