            size = 0.05;
            for (k = 0; k < 80; k++)
            {
                if (fabs(x) < size && fabs(y) < size)
                {
                    cout << "♥";
                }
                else
                {
                    cout << ' ';
                }
                x += size;
            }
            cout << endl;
            y += size;
            x = 16 * pow(sin(angle), 3);
        }
    }
}

int main()
{
    drawHeart();
    return 0;
