// App.js
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [accounts, setAccounts] = useState([]);
  const [newAccount, setNewAccount] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/accounts").then((response) => {
      setAccounts(response.data);
    });
  }, []);

  const addAccount = () => {
    axios.post("http://localhost:5000/accounts", { account: newAccount }).then((response) => {
      setAccounts([...accounts, response.data]);
    });
  };

  return (
    <div>
      <h1>测试账户列表</h1>
      <ul>
        {accounts.map((account) => (
          <li key={account.id}>{account.name}</li>
        ))}
      </ul>
      <input type="text" value={newAccount} onChange={(e) => setNewAccount(e.target.value)} />
      <button onClick={addAccount}>添加测试账户</button>
    </div>
  );
}

export default App;
