using System;
using System.Collections.Generic;
using System.IO;

namespace TextFileProcessor
{
    class Program
    {
        static void Main(string[] args)
        {
            // Get total database file path
            Console.Write("Enter total database file path: ");
            string totalFilePath = Console.ReadLine();

            // Get file to be deduplicated path
            Console.Write("Enter file to be deduplicated path: ");
            string deduplicateFilePath = Console.ReadLine();

            // Get output file path for new data
            Console.Write("Enter output file path for new data: ");
            string newFilePath = Console.ReadLine();

            // Get output file path for duplicate data
            Console.Write("Enter output file path for duplicate data: ");
            string duplicateFilePath = Console.ReadLine();

            // Create HashSet to store total database
            HashSet<string> totalDatabase = new HashSet<string>();

            // Read total database file and store in HashSet
            using (StreamReader reader = new StreamReader(totalFilePath))
            {
                while (!reader.EndOfStream)
                {
                    totalDatabase.Add(reader.ReadLine());
                }
            }

            // Create HashSet to store deduplicate file
            HashSet<string> deduplicateFile = new HashSet<string>();

            // Read deduplicate file and store in HashSet
            using (StreamReader reader = new StreamReader(deduplicateFilePath))
            {
                while (!reader.EndOfStream)
                {
                    deduplicateFile.Add(reader.ReadLine());
                }
            }

            // Find new data
            HashSet<string> newData = new HashSet<string>(deduplicateFile.Except(totalDatabase));

            // Output new data to file
            using (StreamWriter writer = new StreamWriter(newFilePath))
            {
                foreach (string value in newData)
                {
                    writer.WriteLine(value);
                }
            }

            // Find duplicate data
            HashSet<string> duplicateData = new HashSet<string>(deduplicateFile.Intersect(totalDatabase));

            // Output duplicate data to file
            using (StreamWriter writer = new StreamWriter(duplicateFilePath))
            {
                foreach (string value in duplicateData)
                {
                    writer.WriteLine(value);
                }
            }

            Console.WriteLine("Deduplication complete.");
        }
    }
}
