            Console.Write("Enter input file path for List A: ");
            string listAFilePath = Console.ReadLine();

            // Get input file path for List B
            Console.Write("Enter input file path for List B: ");
            string listBFilePath = Console.ReadLine();

            // Get output file path for B - A
            Console.Write("Enter output file path for B - A: ");
            string complementFilePath = Console.ReadLine();

            // Get output file path for A ∩ B
            Console.Write("Enter output file path for A ∩ B: ");
            string intersectionFilePath = Console.ReadLine();

            // Read List A and store in HashSet
            HashSet<string> setA = new HashSet<string>();
            using (StreamReader reader = new StreamReader(listAFilePath))
            {
                while (!reader.EndOfStream)
                {
                    setA.Add(reader.ReadLine());
                }
            }

