                {
                    writer.WriteLine(item);
                }
            }

            // Find items in A ∩ B and output to intersection file
            var intersectionList = setA.Intersect(setB).ToList();
            using (StreamWriter writer = new StreamWriter(intersectionFilePath))
            {
                foreach (string item in intersectionList)
                {
                    writer.WriteLine(item);
                }
            }

