```java
import java.io.File;
import java.io.FileNotFoundException;
import java.io.PrintWriter;
import java.util.Scanner;

public class ArraySorter {

    public static void main(String[] args) {
        int[] nums = new int[100];
        int[] count = new int[100];
        int totalRecords = readData(nums, count);

        sortArray(nums, count, totalRecords);
        printArray(nums, count, totalRecords);
    }

    public static int readData(int[] nums, int[] count) {
        int records = 0;
        try {
            Scanner scanner = new Scanner(new File("numInput.txt"));
            while (scanner.hasNextInt()) {
                int num = scanner.nextInt();
                int index = linearSearch(nums, num, records);
                if (index == -1) {
                    nums[records] = num;
                    count[records] = 1;
                    records++;
                } else {
                    count[index]++;
                }
            }
            scanner.close();
        } catch (FileNotFoundException e) {
            System.err.println("File not found");
        }
        return records;
    }

    public static int linearSearch(int[] array, int key, int pos) {
        for (int i = 0; i < pos; i++) {
            if (array[i] == key) {
                return i;
            }
        }
        return -1;
    }

    public static void sortArray(int[] primary, int[] secondary, int k) {
        for (int i = 0; i < k - 1; i++) {
            for (int j = 0; j < k - i - 1; j++) {
                if (primary[j] > primary[j + 1]) {
                    int tempPrimary = primary[j];
                    primary[j] = primary[j + 1];
                    primary[j + 1] = tempPrimary;

                    int tempSecondary = secondary[j];
                    secondary[j] = secondary[j + 1];
                    secondary[j + 1] = tempSecondary;
                }
            }
        }
    }

    public static void printArray(int[] nums, int[] count, int totalRecords) {
        try {
            PrintWriter writer = new PrintWriter(new File("numOutput.txt"));
            writer.println("number   count");
            for (int i = 0; i < totalRecords; i++) {
                writer.printf("%5d%9d%n", nums[i], count[i]);
            }
            writer.close();
        } catch (FileNotFoundException e) {
            System.err.println("File not found");
        }
    }
}
```

```java
import java.io.File;
import java.io.FileNotFoundException;
import java.io.PrintWriter;
import java.util.Scanner;

public class ArraySorter {

    // Main method
    public static void main(String[] args) {
        int[] nums = new int[100]; // array to store unique numbers
        int[] count = new int[100]; // array to store the count of each unique number
        int totalRecords = readData(nums, count); // read data from input file and get the total count of unique numbers

        sortArray(nums, count, totalRecords); // sort nums array and keep count array synchronized
        printArray(nums, count, totalRecords); // print nums and count arrays to output file
    }

    // Read data from input file and populate nums and count arrays
    public static int readData(int[] nums, int[] count) {
        int records = 0;
        try {
            Scanner scanner = new Scanner(new File("numInput.txt"));
            while (scanner.hasNextInt()) {
                int num = scanner.nextInt();
                int index = linearSearch(nums, num, records); // search for the current number in nums array
                if (index == -1) {
                    // current number not found in nums, add it
                    nums[records] = num;
                    count[records] = 1;
                    records++; // increment the total count of unique numbers
                } else {
                    // current number found in nums, increase the count
                    count[index]++;
                }
            }
            scanner.close();
        } catch (FileNotFoundException e) {
            System.err.println("File not found");
        }
        return records;
    }

    // Linear search method to find the index of a given key in an array, returns -1 if not found
    public static int linearSearch(int[] array, int key, int pos) {
        for (int i = 0; i < pos; i++) {
            if (array[i] == key) {
                return i;
            }
        }
        return -1;
    }

    // Sort primary array (nums) in ascending order and keep secondary array (count) synchronized
    public static void sortArray(int[] primary, int[] secondary, int k) {
        for (int i = 0; i < k - 1; i++) {
            for (int j = 0; j < k - i - 1; j++) {
                if (primary[j] > primary[j + 1]) {
                    // swap elements in primary
                    int tempPrimary = primary[j];
                    primary[j] = primary[j + 1];
                    primary[j + 1] = tempPrimary;

                    // swap elements in secondary
                    int tempSecondary = secondary[j];
                    secondary[j] = secondary[j + 1];
                    secondary[j + 1] = tempSecondary;
                }
            }
        }
    }

    // Print the sorted nums and count arrays to an output file
    public static void printArray(int[] nums, int[] count, int totalRecords) {
        try {
            PrintWriter writer = new PrintWriter(new File("numOutput.txt"));
            writer.println("number   count");
            for (int i = 0; i < totalRecords; i++) {
                writer.printf("%5d%9d%n", nums[i], count[i]);
            }
            writer.close();
        } catch (FileNotFoundException e) {
            System.err.println("File not found");
        }
    }
}
```

