			tmp.Assign(rhs.begin(), rhs.end());
			swap(tmp);
		}

		return *this;
	}

	void swap(SingleLinkedList& other) noexcept {
		size_t tmp = size_;
		size_ = other.size_;
		other.size_ = tmp;

		Node* node_ptr = head_.next_node;
		head_.next_node = other.head_.next_node;
		other.head_.next_node = node_ptr;
	}

