            cache[i][j].valid = 1;
            cache[i][j].last_used = 0;
        }
    }
}

// Generate data item id following hot and cold distribution
int generate_data_item() {
    double hot_prob = uniform(0, 1);
    int data_item_id;

    if (hot_prob <= HOT_DATA_ACCESS_PROB) {
        data_item_id = rand() % HOT_DATA_ITEMS;
    } else {
        data_item_id = HOT_DATA_ITEMS + rand() % COLD_DATA_ITEMS;
    }

    return data_item_id;
}

