int check_cache(int node, int data_item) {
    for (int i = 0; i < CACHE_SIZE; i++) {
        if (cache[node][i].data_item == data_item && cache[node][i].valid) {
            cache[node][i].last_used = current_time;
            return 1;
        }
    }
    return 0;
}

// Generate update arrival
void generate_update(double mean_update_arrival_time) {
    int data_item = generate_data_item();
    double hot_prob = uniform(0, 1);

    if (hot_prob <= HOT_DATA_UPDATE_PROB) {
        // Choose a hot data item for update
        data_item = rand() % HOT_DATA_ITEMS;
    } else {
        // Choose a cold data item for update
        data_item = HOT_DATA_ITEMS + rand() % COLD_DATA_ITEMS;
    }

    // Update the server database and invalidate the cache
    database[data_item]++;
    invalidate_cache(data_item);

    // Schedule the next update arrival
    hold(expntl(mean_update_arrival_time));
    generate_update(mean_update_arrival_time);
}

// Invalidate cache for the updated data item in each node
void invalidate_cache(int data_item) {
    for (int i = 0; i < NUM_CLIENTS; i++) {
        for (int j = 0; j < CACHE_SIZE; j++) {
            if (cache[i][j].data_item == data_item) {
                cache[i][j].valid = 0;
                break;
            }
        }
    }
