```c
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include "csim.h"

// Cache struct
typedef struct {
    int id;
    int timestamp;
} CacheEntry;

void add_random_data_item_to_cache(CsSet *cache);
void replace_data_item_in_cache(CsSet *cache, int item_id);
int pick_data_item(int hot_prob);

CS_EVENT_ROUTINE(query_event) {
    CsSet *cache = (CsSet *) user_data;
    if (cs_set_is_empty(cache)) {
        printf("Cache is empty!\n");
        cs_event_schedule_relative(query_event, 5, cache);
        return 0;
    }
    int query_id = pick_data_item(80);
    CsQueueNode *node = cache->queue->head;
    CacheEntry *entry = NULL;
    while (node != NULL) {
        entry = (CacheEntry *) (((CsEntity *) node->userdata)->attribute_queue->userdata);
        if (entry->id == query_id)
            break;
        entry = NULL;
        node = node->next;
    }
    if (entry != NULL) {
        printf("Cache hit!\n");
        // Cache hit; do something
        cs_event_schedule_relative(query_event, 5, cache);
    } else {
        printf("Cache miss!\n");
        // Cache miss; request data from server
        replace_data_item_in_cache(cache, query_id);
        cs_event_schedule_relative(query_event, 5, cache);
    }
    return 0;
}

void add_random_data_item_to_cache(CsSet *cache) {
    CsEntity *new_data_item = cs_entity_new();
    cs_attribute_add(new_data_item, "cache_entry");
    CacheEntry *new_entry = (CacheEntry *) malloc(sizeof(CacheEntry));
    new_entry->id = rand() % 500;
    new_entry->timestamp = NOW;
    new_data_item->attribute_queue->userdata = (void *) new_entry;
    cs_set_file(cache, new_data_item);
}

void replace_data_item_in_cache(CsSet *cache, int item_id) {
    CacheEntry *entry = NULL;
    CsQueueNode *node = cache->queue->head;
    while (node != NULL) {
        entry = (CacheEntry *)(((CsEntity *)node->userdata)->attribute_queue->userdata);
        if (entry->id == -1)
            break;  
        entry = NULL;
        node = node->next;
    }
    if (entry != NULL) {
        entry->id = item_id;
        entry->timestamp = NOW;
    }
}

int pick_data_item(int hot_prob) {
    int prob = rand() % 100;
    if (prob < hot_prob)
        return rand() % 50;
    return 50 + rand() % (500 - 50);
}

int main() {
    srand(time(NULL));
    cs_init();
    CsEvent *query_event = cs_event_new(query_event);
    int num_clients = 5;
    int cache_size = 100;

    for (int client = 0; client < num_clients; client++) {
        // Initialize cache for each client
        CsSet *cache = cs_set_new(FIFO);
        for (int i = 0; i < cache_size; i++) {
            add_random_data_item_to_cache(cache);
        }
        // Cancel the previously scheduled event
        cs_event_schedule(query_event, rand() % 100, cache);
    }
    cs_start_simulation();
    return 0;
}
```

