    int count = 1; // Устанавливаем номер элемента списка по умолчанию
    while (true) { // Запускаем бесконечный цикл для добавления элементов в список
        cout << "[" << count << "]: "; // Номер элемента
        getline(cin, input);
        if (input == "") { // Если элемент не введен (пустая строка), прерываем цикл
            break;
        } else { // Иначе, добавляем элемент в список и увеличиваем номер элемента
            lista.push_back(input);
