        }
    }

    if (lista.empty()) { // если список пустой, выводим сообщение об ошибке и завершаем программу
        cerr << "Ошибка: список пуст!" << endl;
        return 1;
    }

    sort(lista.begin(), lista.end()); // Сортируем список перед поиском

    while (true) { // бесконечный цикл
        cout << "Введите элемент, который нужно найти (для выхода введите /q):" << endl;
        getline(cin, item); // Получаем ввод пользователя
        if (item == "/q") {
            break;
        }
        if (item.empty()) { // Если строка пустая, то цикл заново
            cerr << "Ошибка: введена пустая строка!" << endl;
