ClassPathBeanDefinitionScanner scanner = new ClassPathBeanDefinitionScanner(registrar.getRegistry(), false);
scanner.setResourceLoader(env.getConfig().getGlobalJobParameters().getClassLoader());
scanner.scan(registrar.getPackageName());
MybatisMapperRegistry.INSTANCE.register(MyMapper.class, () -> {
    MybatisSqlSessionFactory mybatisSqlSessionFactory = new MybatisSqlSessionFactory();
    mybatisSqlSessionFactory.setDataSource(getDataSource());
    mybatisSqlSessionFactory.setConfiguration(properties);
    return mapperFactory.getMapper(MyMapper.class, mybatisSqlSessionFactory);
});
MapperRegistry.INSTANCE.register(UserMapper.class, () -> new UserMapperImpl(MyMapperRegistry.INSTANCE.get(MyMapper.class), UserDO.class));
GlobalConfig globalConfig = new GlobalConfig();
globalConfig.setMetaObjectHandler(new MyMetaObjectHandler());
GlobalConfigUtils.setGlobalConfig(globalConfig);

