    if (birthday.Value.Month == 2 && birthday.Value.Day > 28)
        return new DateTime(2020, birthday.Value.Month, birthday.Value.Day);
    else
        return new DateTime(DateTime.Now.Year, birthday.Value.Month, birthday.Value.Day);
}
