public static void EmbedPrimitivesToCell(List<EntityGeometry> entityGeometry, McTable _table, int rowIndex, int colIndex)
{
    using (var transaction = beginTransaction())
    {
        // Get the active BlockTable
        var blockTable = Transaction.GetObject(Doc.Database.BlockTableId, OpenMode.ForRead) as BlockTable;

        // Create a new BlockTableRecord to hold our primitives
        using (var btr = new BlockTableRecord())
        {
            // Add each entity in entityGeometry to the BlockTableRecord
            foreach (var entity in entityGeometry)
            {
                btr.AppendEntity(entity);
                transaction.AddNewlyCreatedDBObject(entity, true);
            }

            // Get the McTable's active BlockReference
            var tableBref = _table.TableBlockReference;

            // Get the table's position and scale factors
            var tablePosition = tableBref.Position;
            var xscale = tableBref.ScaleFactors.X;
            var yscale = tableBref.ScaleFactors.Y;

            // Calculate the cell's position and size based on the row and column indices
            var cellWidth = _table.TableColumns[colIndex].Width * xscale;
            var cellHeight = _table.TableRows[rowIndex].Height * yscale;
            var cellX = tablePosition.X + _table.TableLeftOffset + _table.TableColumns.Take(colIndex).Sum(c => c.Width) * xscale;
            var cellY = tablePosition.Y - _table.TableTopOffset - _table.TableRows.Take(rowIndex).Sum(r => r.Height) * yscale;

            // Create a new BlockReference to hold the BlockTableRecord
            using (var bref = new BlockReference(new Point3d(cellX, cellY, 0), btr.ObjectId))
            {
                // Set the BlockReference's scale factors
                bref.ScaleFactors = new Vector3d(xscale, yscale, 1);

                // Add the BlockReference to the McTable's BlockTableRecord
                var tableRecord = Transaction.GetObject(_table.TableBlockReference.BlockTableRecord, OpenMode.ForWrite) as BlockTableRecord;
                tableRecord.AppendEntity(bref);
                transaction.AddNewlyCreatedDBObject(bref, true);

                // Save the changes and commit the transaction
                transaction.Commit();
            }
        }
    }
