    double b = 2 - a + floor(a / 4);
    return (floor(365.25 * (year + 4716)) + floor(30.6001 * (month + 1)) + day + b - 1524.5);
}

double calc_sun_position(double jd, double n, double *decl) {
    double t = (jd - 2451545) / 36525;
