    double C = (1.914 * sin_M) + (0.020 * sin_2M) + (0.0003 * sin_3M);
    double sun_long = L0 + C;
    double v = M + C + 102.937;
    double R = 1.00014 - (0.01671 * cos(DEG_TO_RAD * (M + C))) + (0.00014 * cos(DEG_TO_RAD * (2 * M + 2 * C)));
    n = fix_hour(n - R + 0.00569 - (0.004178 * sin(DEG_TO_RAD * v)));

