переведи с perl на python sub _get_max_pk {
    my $self = shift;
    my $instr = shift;
    my $dbh_dst = shift;

    # если инструкция перезаписываемая, то максимальный прошлый id - всегда 0
    return undef if $instr->{overwrite};
    return undef if $instr->{is_diff_required};

    my $max_pk = $self->_get_max_col(
        dbh => $instr->{dbh_dst },
        tb  => $instr->{ dst_tb_original },
        col => $instr->{ pk     },
    );
    return $max_pk if not $instr->{is_history};


    my $max_pk__history = $self->_get_max_col(
        dbh => $instr->{dbh_dst },
        tb  => $instr->{ dst_tb },
        col => $instr->{ pk     },
    );
    $max_pk = $max_pk__history  if $max_pk__history and $max_pk__history > $max_pk;
    return $max_pk;
 }

def get_max_pk(self, instr, dbh_dst):
    if instr["overwrite"] or instr["is_diff_required"]:
        return None

    max_pk = self.get_max_col(dbh=instr["dbh_dst"], tb=instr["dst_tb_original"], col=instr["pk"])
    if not instr["is_history"]:
        return max_pk

    max_pk_history = self.get_max_col(dbh=instr["dbh_dst"], tb=instr["dst_tb"], col=instr["pk"])
    if max_pk_history and max_pk_history > max_pk:
        max_pk = max_pk_history
    return max_pk

