%% Project images onto the eigenvectors
train_proj = train_set' * coeff; % Training feature matrix
test_proj = test_set' * coeff; % Testing feature matrix

%% Perform feature selection
n_features = 6;
features_selected = sequentialfs(@(train, test, train_labels, test_labels) linear_classifier(train, test, train_labels, test_labels), train_proj, m(1:2:72), 'NFeatures', n_features);

