    0.1,
    1000
);

const axesHalper = new THREE.AxesHelper(5);
scene.add(axesHalper);
camera.position.z(5);

renderer.render(scene, camera);

Приложение, приведенное в вашем сообщении, не отображает ничего на канвасе, потому что не добавлено ни одного объекта на сцену. Таким образом, информации о том, что необходимо отображать, просто нет. 

Кроме того, есть несколько ошибок в вашем коде:
- Ошибка в методе задания позиции камеры. Вместо `camera.position.z(5)` нужно использовать `camera.position.z = 5`.
- Вместо `axesHalper` нужно использовать `axesHelper`.

Давайте добавим в ваш код объект на сцену - куб, и поставим его посередине:

```
const cubeGeometry = new THREE.BoxGeometry(1, 1, 1);
const cubeMaterial = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
const cube = new THREE.Mesh(cubeGeometry, cubeMaterial);
scene.add(cube);

// Перемещаем куб на позицию, чтобы он был виден в камере
cube.position.z = -5;
```

Итоговый код, который выводит на канвас зеленый куб:

```
import * as THREE from 'three';

const renderer = new THREE.WebGL1Renderer();

renderer.setSize(window.innerWidth, window.innerHeight);

document.body.appendChild(renderer.domElement);

const scene = new THREE.Scene();
const camera = new THREE.PerspectiveCamera(
    75,
    window.innerWidth/ window.innerHeight,
    0.1,
    1000
);

const axesHelper = new THREE.AxesHelper(5);
scene.add(axesHelper);
camera.position.z = 5;

const cubeGeometry = new THREE.BoxGeometry(1, 1, 1);
const cubeMaterial = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
const cube = new THREE.Mesh(cubeGeometry, cubeMaterial);
scene.add(cube);

// Перемещаем куб на позицию, чтобы он был виден в камере
cube.position.z = -5;

renderer.render(scene, camera);
``` 

Теперь на канвасе должен отображаться зеленый куб, и оси координат.

