            for (int j = 0; j < 8; j++) {
                System.out.printf("%4d", nlat[i][j]);
            }
            System.out.println();
        }
    }

    public static int[][] lat(int[] sbox) {
        int[][] nlat = new int[8][8];

        for (int i = 0; i < 8; i++) {
            for (int j = 0; j < 8; j++) {
                int count = 0;
                for (int k = 0; k < 8; k++) {
                    int x = k;
                    int xMask = Integer.bitCount(x & i);
                    int y = sbox[k];
                    int yMask = Integer.bitCount(y & j);

                    if ((xMask - yMask) % 2 == 0) {
                        count++;
                    }
                }
                nlat[i][j] = count - 4;
            }
        }

        return nlat;
    }
}
